import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import classnames from "classnames";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import Collapse from "@material-ui/core/Collapse";
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import red from "@material-ui/core/colors/red";
import EditIcon from "@material-ui/icons/Edit";
import ShareIcon from "@material-ui/icons/Share";
import AccessTime from "@material-ui/icons/AccessTime";
import LocalShipping from "@material-ui/icons/LocalShipping";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendar, faClock } from "@fortawesome/free-solid-svg-icons";

const styles = theme => ({
  card: {
    maxWidth: 400,
    marginLeft: 40
  },
  media: {
    height: 0,
    paddingTop: "56.25%" // 16:9
  },
  actions: {
    display: "flex"
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest
    })
  },
  expandOpen: {
    transform: "rotate(180deg)"
  },
  avatar: {
    backgroundColor: red[500]
  },

  icon: {
    fontSize: 100,
    textAlign: "center",
    padding: 50
  },
  pending: {
    color: "#f44336"
  },
  arrived: {
    color: "#4caf50"
  }
});

function getDate(dateString) {
  var today = new Date(dateString);
  var dd = String(today.getDate()).padStart(2, "0");
  var mm = String(today.getMonth() + 1).padStart(2, "0"); //January is 0!
  var yyyy = today.getFullYear();
  var hh = String(today.getHours());
  var min = String(today.getMinutes());
  var sec = String(today.getSeconds());

  var formatedDate =
    dd + "." + mm + "." + yyyy + " " + hh + ":" + min + ":" + sec;
  return formatedDate;
}

class InfoCard extends React.Component {
  state = {
    expanded: false,
    expandedInfo: (
      <div>
        <p>
          <b>Weight: </b>
          {this.props.transactionData.weight}
        </p>

        <p>
          <b>Weight: </b>
          <a
            href="https://www.latlong.net/c/?lat=&long=8.682127"
            target="_blank"
          >
            ({this.props.transactionData.location.lat},{" "}
            {this.props.transactionData.location.long})
          </a>
          {this.props.transactionData.weight}
        </p>
      </div>
    ),
    pending: true
  };

  handleExpandClick = () => {
    this.setState(state => ({ expanded: !state.expanded }));
  };

  pendignState = () => {
    if (this.state.pending) {
      return (
        <IconButton
          className={this.props.classes.pending}
          aria-label="Item Pending"
        >
          <LocalShipping />
          <Typography className={this.props.classes.pending}>
            &nbsp;Item pending
          </Typography>
        </IconButton>
      );
    } else {
      return (
        <IconButton
          className={this.props.classes.arrived}
          aria-label="LocalShipping"
        >
          <LocalShipping />
          <Typography className={this.props.classes.arrived}>
            &nbsp;Item has arrived
          </Typography>
        </IconButton>
      );
    }
  };

  render() {
    const { classes } = this.props;
    const transactionData = this.props.transactionData;

    return (
      <Card className={classes.card}>
        <CardHeader
          avatar={
            <Avatar aria-label="Recipe" className={classes.avatar}>
              I
            </Avatar>
          }
          action={
            <IconButton>
              <MoreVertIcon />
            </IconButton>
          }
          title={transactionData.id}
          subheader={getDate(transactionData.date)}
        />
        <CardMedia
          className={classes.media}
          image="https://images.unsplash.com/photo-1552901633-210088e17486?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80"
          title="Paella dish"
        />
        <CardContent>
          <Typography component="p"> {this.pendignState()} </Typography>
        </CardContent>
        <CardActions className={classes.actions} disableActionSpacing>
          <IconButton aria-label="Add to favorites">
            <EditIcon />
          </IconButton>
          <IconButton aria-label="Share">
            <ShareIcon />
          </IconButton>
          <IconButton
            className={classnames(classes.expand, {
              [classes.expandOpen]: this.state.expanded
            })}
            onClick={this.handleExpandClick}
            aria-expanded={this.state.expanded}
            aria-label="Show more"
          >
            <ExpandMoreIcon />
          </IconButton>
        </CardActions>
        <Collapse in={this.state.expanded} timeout="auto" unmountOnExit>
          <CardContent>
            <Typography paragraph> {this.state.expandedInfo} </Typography>
          </CardContent>
        </Collapse>
      </Card>
    );
  }
}

InfoCard.propTypes = {
  classes: PropTypes.object.isRequired
};

export default withStyles(styles)(InfoCard);

const clock = String(
  <span>
    <FontAwesomeIcon icon={faClock} />
  </span>
);
